import React, { Component } from 'react';

import {
  ResponsiveContainer, LineChart, Line, XAxis, YAxis, CartesianGrid, Tooltip, Legend,
} from 'recharts';
import axiosbase from 'axios';
import moment from 'moment';

const axios = axiosbase.create({
  baseURL: 'http://localhost:3000',
  headers: {
    'Content-Type': 'application/json',
  },
  responseType: 'json',
})


class Home extends Component {
  constructor(props){
    super(props)
    this.state = {
      data: [],
      // year: [],
      // month: [],
      // energy_production: [],
      lists: [],
    };
  }

  componentDidMount() {
    axios
    .get('/')
    .then((res) => {
      console.log(res.data.data);
      this.setState({ lists: res.data.data });
      // console.dir(res.data.data.energy_production[0]);

      res.data.data.map((data) => {
        this.state.data.push({date: moment(data.date).unix() * 1000, Cambridge: data.cambridge, London: data.london, Oxford: data.oxford})
      })
    })
    .catch((err) => {
      console.log(err) // 失敗
    });
  }

  render () {
    //// - Memo -
    //// 1. レスポンスは返ってきている
    //// 2. 一応画面描画もできている
    //// 3. API設計し直した方が早いかも...
