import React, { Component } from 'react';

import {
  ResponsiveContainer, LineChart, Line, XAxis, YAxis, CartesianGrid, Tooltip, Legend,
} from 'recharts';
import axiosbase from 'axios';
import moment from 'moment';

const axios = axiosbase.create({
  baseURL: 'http://localhost:3000',
  headers: {
    'Content-Type': 'application/json',
  },
  responseType: 'json',
})


class Home extends Component {
  constructor(props){
    super(props)
    this.state = {
      data: [],
      // year: [],
      // month: [],
      // energy_production: [],
      lists: [],
    };
  }

  componentDidMount() {
    axios
    .get('/')
    .then((res) => {
      console.log(res.data);
      this.setState({ lists: res.data });
      // console.dir(res.data.data.energy_production[0]);
    })
    .catch((err) => {
      console.log(err) // 失敗
    });
  }

  render () {
    //// - Memo -
    //// 1. レスポンスは返ってきている
    //// 2. 一応画面描画もできている
    //// 3. API設計し直した方が早いかも...
    //// this.state.dataを取得してエレメント作らないといけない
    //// 難しいので取得レスポンスの形を考え直した方が早いかも
    //// res.data.dataでレスポンス内容を取得可能
    //// - 2020/07/12 -

    // const data = [
    //   {
    //     Oxford: this.state.data.data.energy_production
    //   },
    //   {
    //     Oxford: this.state.data.data.energy_production
    //   },
    //   {
    //     Oxford: this.state.data.data.energy_production
    //   }
    // ];

    const data = [
      {
        name: '2020-07', London: 4000, Oxford: 2400, amt: 2400,
      },
      {
        name: '2020-08', London: 3000, Oxford: 1398, amt: 2210,
      },
      {
        name: '2020-09', London: 2000, Oxford: 9800, amt: 2290,
      },
      {
        name: '2020-10', London: 2780, Oxford: 3908, amt: 2000,
      },
      {
        name: '2020-11', London: 1890, Oxford: 4800, amt: 2181,
      },
      {
        name: '2020-12', London: 2390, Oxford: 3800, amt: 2500,
      },
      {
        name: '2021-01', London: 3490, Oxford: 4300, amt: 2100,
      },
    ];

    // {this.state.res.data.map((data) => {
    //   data.push({date: moment('2020-07-01').unix() * 1000, Oxford: lists.oxford})
    // })}

    // const data = [
    //   { date: moment('2018-12-01').unix() * 1000, 最高気温: 10, 最低気温: 1 },
    //   { date: moment('2018-12-02').unix() * 1000, 最高気温: 12, 最低気温: 4 },
    //   { date: moment('2018-12-03').unix() * 1000, 最高気温: 18, 最低気温: 8 },
    //   { date: moment('2018-12-04').unix() * 1000, 最高気温: 10, 最低気温: 0 },
    //   { date: moment('2018-12-05').unix() * 1000, 最高気温: 9, 最低気温: 1 },
    //   { date: moment('2018-12-06').unix() * 1000, 最高気温: 13, 最低気温: 2 },
    //   { date: moment('2018-12-07').unix() * 1000, 最高気温: 16, 最低気温: 5 },
    // ];

    return (
      <div>
      <LineChart
        width={500}
        height={300}
        data={data}
        margin={{
          top: 5, right: 30, left: 20, bottom: 5,
        }}
      >
        <CartesianGrid strokeDasharray="3 3" />
        <XAxis dataKey="name" />
        <YAxis />
        <Tooltip />
        <Legend />
        <Line type="monotone" dataKey="Oxford" stroke="#8884d8" activeDot={{ r: 8 }} />
        {/* <Line type="monotone" dataKey="London" stroke="#82ca9d" /> */}
        <Line type="monotone" dataKey="London" stroke="#8884d8" activeDot={{ r: 8 }} />
        {/* <Line type="monotone" dataKey="Oxford" stroke="#82ca9d" />
        <Line type="monotone" dataKey="London" stroke="#82ca9d" /> */}
      </LineChart>
      </div>
    );
  }
}

export default Home;
